	&lt;TD class="tr18 td72"&gt;&lt;SPAN class="p15 ft62"&gt;arkivmyndigheterna (Ku 2003:03)&lt;/SPAN&gt; &lt;/TD&gt;
	&lt;TD class="tr18 td73"&gt;&lt;SPAN class="p5 ft2"&gt;&amp;nbsp;&lt;/SPAN&gt; &lt;/TD&gt;
