changeSet.sql:
-- Verify views exist and can be queried SELECT 'Ministry Views verification:' AS check_description;
-- Check ministry assignment count SELECT COUNT(DISTINCT org_code) AS ministry_count, 'Ministry assignments (assignment_type = Departement)' AS description FROM assignment_data WHERE assignment_type = 'Departement' AND org_code IS NOT NULL;
-- Check view_ministry_effectiveness_trends row count SELECT COUNT(*) AS view_row_count, 'Rows in view_ministry_effectiveness_trends' AS description FROM view_ministry_effectiveness_trends;
-- Check view_ministry_productivity_matrix row count SELECT COUNT(*) AS view_row_count, 'Rows in view_ministry_productivity_matrix' AS description FROM view_ministry_productivity_matrix;
-- Check view_ministry_risk_evolution row count SELECT COUNT(*) AS view_row_count, 'Rows in view_ministry_risk_evolution' AS description FROM view_ministry_risk_evolution;
-- If view_ministry_effectiveness_trends has data, show sample SELECT org_code, short_code, name, year, quarter, documents_produced, active_members, productivity_level FROM view_ministry_effectiveness_trends ORDER BY year DESC, quarter DESC, documents_produced DESC LIMIT 5;
