changeSet.sql:
-- Verify view exists and can be queried SELECT 'Crisis Resilience View verification:' AS check_description;
-- Check source data count SELECT COUNT(*) AS vote_count, 'Votes in last 5 years' AS description FROM vote_data WHERE vote_date >= CURRENT_DATE - INTERVAL '5 years';
-- Check active politician count SELECT COUNT(*) AS active_count, 'Active politicians' AS description FROM person_data WHERE status IN ('active', 'Active', 'ACTIVE');
-- Check view row count SELECT COUNT(*) AS view_row_count, 'Rows in view_riksdagen_crisis_resilience_indicators' AS description FROM view_riksdagen_crisis_resilience_indicators;
-- If view has data, show sample SELECT person_id, first_name, last_name, party, crisis_period_votes, normal_period_votes, resilience_classification FROM view_riksdagen_crisis_resilience_indicators ORDER BY crisis_period_votes DESC LIMIT 5;
