changeSet.sql:
DO $$
DECLARE
    v_performance_exists BOOLEAN;
    v_coalition_exists BOOLEAN;
    v_electoral_exists BOOLEAN;
BEGIN
    SELECT EXISTS(
        SELECT 1 FROM information_schema.views 
        WHERE table_name = 'view_riksdagen_party_longitudinal_performance'
    ) INTO v_performance_exists;

    SELECT EXISTS(
        SELECT 1 FROM information_schema.views 
        WHERE table_name = 'view_riksdagen_party_coalition_evolution'
    ) INTO v_coalition_exists;

    SELECT EXISTS(
        SELECT 1 FROM information_schema.views 
        WHERE table_name = 'view_riksdagen_party_electoral_trends'
    ) INTO v_electoral_exists;

    IF v_performance_exists AND v_coalition_exists AND v_electoral_exists THEN
        RAISE NOTICE ' All 3 OPTIMIZED party longitudinal views created successfully';
        RAISE NOTICE '  OPTIMIZATIONS APPLIED:';
        RAISE NOTICE '  - Builds on existing views: view_riksdagen_vote_data_ballot_party_summary_annual, view_party_performance_metrics';
        RAISE NOTICE '  - Advanced statistics: RANK, PERCENT_RANK, NTILE, LAG, LEAD, STDDEV_POP';
        RAISE NOTICE '  - Swedish parliament cycles: autumn (Sep-Jan) &amp; spring (Jan-Aug) semesters';
        RAISE NOTICE '  - Comprehensive KPIs: 30+ metrics in performance view, 25+ in coalition view, 28+ in electoral view';
        RAISE NOTICE '  - Predictive indicators: forecast scores, momentum z-scores, early warning flags';
        RAISE NOTICE '  - Trend detection: Multi-dimensional trajectory classification, volatility assessments';
        RAISE NOTICE '  Coverage: 2002-2026 (7 election cycles, semester-level granularity)';
    ELSE
        RAISE WARNING 'Some optimized party longitudinal views failed to create';
        IF NOT v_performance_exists THEN
            RAISE WARNING '  - view_riksdagen_party_longitudinal_performance missing';
        END IF;
        IF NOT v_coalition_exists THEN
            RAISE WARNING '  - view_riksdagen_party_coalition_evolution missing';
        END IF;
        IF NOT v_electoral_exists THEN
            RAISE WARNING '  - view_riksdagen_party_electoral_trends missing';
        END IF;
    END IF;
END $$;
