          WHERE (pdf.party IS NOT NULL)
          GROUP BY ecp.election_cycle_id, ecp.cycle_year, ecp.calendar_year,
                CASE
                    WHEN ((pdf.decision_month_num >= (9)::numeric) OR (pdf.decision_month_num <= (1)::numeric)) THEN 'autumn'::text
