            WHEN (seat_count_proxy >= 50) THEN 'LARGE_PARTY'::text
            WHEN (seat_count_proxy >= 30) THEN 'MEDIUM_PARTY'::text
            WHEN (seat_count_proxy >= 15) THEN 'SMALL_PARTY'::text
